targets.expr:
sum (
    sum by (namespace,pod) (kube_pod_container_resource_requests{resource="cpu",node=~"$node"})
    * on(namespace, pod) group_left() max by (namespace, pod) (
        kube_pod_status_phase{phase=~"Pending|Running"} == 1
    )
)

sum by(node,type) (
    label_replace(
        sum by(node) (container_memory_working_set_bytes{container!="",node=~"$node"} @ end())
        ,"type","usage","node",".*"
    )
)

sum by(node,type) (
    label_replace(
        sum by(node) (kube_node_status_allocatable{resource="memory",node=~"$node"} @ end())
        ,"type","allocatable","node",".*"
    )
)

sum (
    sum by (namespace,pod) (kube_pod_container_resource_requests{resource="memory",node=~"$node"})
    * on(namespace, pod) group_left() max by (namespace, pod) (
        kube_pod_status_phase{phase=~"Pending|Running"} == 1
    )
)

sum (
    sum by (namespace,pod) (kube_pod_container_resource_limits{resource="memory",node=~"$node"})
    * on(namespace, pod) group_left() max by (namespace, pod) (
        kube_pod_status_phase{phase=~"Pending|Running"} == 1
    )
)
